import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios'; // Assuming you're using Axios for backend calls

const Dashboard = () => {
  const [orders, setOrders] = useState([]);

  // Fetch orders from backend
  useEffect(() => {
    axios.get('/api/orders') // Adjust the API route based on your backend
      .then(response => setOrders(response.data))
      .catch(error => console.error('Error fetching orders:', error));
  }, []);

  const handleStatusChange = (orderId, status) => {
    // Update the order status in the backend
    axios.post(`/api/orders/${orderId}/status`, { status })
      .then(response => {
        // Update the status in the local state
        setOrders(orders.map(order => 
          order.id === orderId ? { ...order, status } : order
        ));
      })
      .catch(error => console.error('Error updating order status:', error));
  };

  return (
    <div className="dashboard">
      <header className="flex justify-between items-center p-5 bg-light-yellow">
        <div className="logo">
          <h1>NextGen</h1>
        </div>
        <nav>
          <Link to="/orders" className="mx-4">Pending Orders</Link>
          <Link to="/profile">Profile</Link>
        </nav>
      </header>

      <div className="container mt-10 p-5">
        <h2 className="text-2xl font-bold mb-4">Pending Orders</h2>

        <div className="orders-list">
          {orders.map((order) => (
            <div key={order.id} className="order-item p-4 mb-4 bg-white rounded shadow-lg">
              <div className="flex items-center mb-2">
                {/* Image holder */}
                <div className="w-16 h-16 bg-gray-200 rounded-full mr-4"></div>

                <div className="order-info">
                  <h3 className="text-lg font-bold">{order.productName}</h3>
                  <p>Quantity: {order.quantity}</p>
                  <p>Delivery Person: {order.deliveryPerson}</p>
                  <p>Address: {order.address}</p>
                </div>
              </div>

              <div className="order-status flex items-center">
                <label className="mr-4">
                  <input
                    type="checkbox"
                    checked={order.status === 'out-for-delivery'}
                    onChange={() => handleStatusChange(order.id, 'out-for-delivery')}
                  />
                  Out for delivery
                </label>
                <label>
                  <input
                    type="checkbox"
                    checked={order.status === 'delivered'}
                    onChange={() => handleStatusChange(order.id, 'delivered')}
                  />
                  Delivered
                </label>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
